<template>
  <div>
    <div>
      <div class="open-door-cen">
        <div>
          <img class="pig_open" src="../../assets/pigopen.png" mode="">
        </div>
        <div class="iconopen_wrap" @click="isOpen">
          <img class="icon_open" src="../../assets/iconopen.png" mode="">
          <div class="open_door">分享</div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import { Dialog, Toast } from 'vant'
import { JSToNativeAPP } from '../../utils/common'
export default {
  data() {
    return {
    }
  },
  methods: {
    isOpen() {
      var _this = this
      Dialog.confirm({
        title: '提示',
        message: '确定要分享吗'
      })
        .then(() => { // 确定
          _this.$post('HouseApi&a=create_url', {
            usernum: JSON.parse(this.$route.query.user).user.usernum
          }).then((res) => {
		  let obj = {
			  title:'访客申请',
			  content:'业主邀请您填写访客信息',
			  url:res.result.url,
			  img:''
		  }
           JSToNativeAPP('share', obj)
          })
        })
        .catch(() => { // 取消
        })
    }
  }
}
</script>

